// ABOUTME: Server-side Auth0 authentication service
// ABOUTME: Handles login, logout, and user session management with Auth0

import { env } from "./env.server";

export async function exchangeCodeForTokens(code: string, redirectUri: string) {
  try {
    const tokenResponse = await fetch(`https://${env.AUTH0_DOMAIN}/oauth/token`, {
      method: "POST",
      headers: {
        "Content-Type": "application/x-www-form-urlencoded",
      },
      body: new URLSearchParams({
        grant_type: "authorization_code",
        client_id: env.AUTH0_CLIENT_ID,
        client_secret: env.AUTH0_CLIENT_SECRET,
        code,
        redirect_uri: redirectUri,
      }),
    });

    if (!tokenResponse.ok) {
      throw new Error(`Token exchange failed: ${tokenResponse.status}`);
    }

    return await tokenResponse.json();
  } catch (error) {
    console.error("Auth0 token exchange failed:", error);
    throw new Error("Failed to exchange authorization code for tokens");
  }
}

export async function getUserInfo(accessToken: string) {
  try {
    const userResponse = await fetch(`https://${env.AUTH0_DOMAIN}/userinfo`, {
      headers: {
        Authorization: `Bearer ${accessToken}`,
      },
    });

    if (!userResponse.ok) {
      throw new Error(`User info failed: ${userResponse.status}`);
    }

    return await userResponse.json();
  } catch (error) {
    console.error("Auth0 user info failed:", error);
    throw new Error("Failed to get user information");
  }
}

export function getLoginUrl(state: string, redirectUri: string) {
  const params = new URLSearchParams({
    response_type: "code",
    client_id: env.AUTH0_CLIENT_ID,
    redirect_uri: redirectUri,
    scope: "openid profile email",
    state,
  });

  return `https://${env.AUTH0_DOMAIN}/authorize?${params.toString()}`;
}

export function getLogoutUrl(returnTo: string) {
  const params = new URLSearchParams({
    client_id: env.AUTH0_CLIENT_ID,
    returnTo,
    federated: "false", // Don't logout of social providers (Google, GitHub, etc)
  });

  return `https://${env.AUTH0_DOMAIN}/logout?${params.toString()}`;
}

