// ABOUTME: Core AI service abstraction using Vercel AI SDK for tool-enabled conversations
// ABOUTME: Provides unified interface for both streaming and non-streaming AI interactions

import { createOpenRouter } from "@openrouter/ai-sdk-provider";
import { type CoreMessage, generateText, streamText } from "ai";
import type { z } from "zod";
import { aiConfig } from "./ai-config.server";
import { env } from "./env.server";

// Tool definition interface that combines schema and implementation
export interface ToolDefinition<T = unknown> {
  description: string;
  parameters: z.ZodSchema<T>;
  execute: (args: T) => Promise<unknown> | unknown;
}

// Configuration for AI service
export interface AIConfig {
  apiKey?: string;
  defaultModel?: string;
  maxRetries?: number;
}

// Options for chat requests
export interface ChatOptions {
  messages: CoreMessage[];
  model?: string;
  tools?: Record<string, ToolDefinition>;
  temperature?: number;
  maxTokens?: number;
  systemPrompt?: string;
}

