        maxOutputTokens: options.maxTokens,
        maxRetries: this.config.maxRetries,
      });

      return result.toTextStreamResponse().body!;
    } catch (error) {
      console.error("AI stream error:", error);
      throw new AIError(
        error instanceof Error ? error.message : "Unknown AI streaming error",
        error,
      );
    }
  }

  // Streaming chat with custom Server-Sent Events formatting
  // Returns both the stream and a promise for the complete text
  async streamSSE(options: ChatOptions): Promise<{
    stream: ReadableStream;
    textPromise: Promise<string>;
  }> {
    const messages = this.buildMessages(options);

    try {
      const result = await streamText({
        model: this.openrouter(options.model || this.config.defaultModel),
        messages,
        tools: this.convertTools(options.tools),
        temperature: options.temperature,
        maxOutputTokens: options.maxTokens,
        maxRetries: this.config.maxRetries,
      });

      const encoder = new TextEncoder();
      let fullText = "";
      let resolveText: (text: string) => void;

      const textPromise = new Promise<string>((resolve) => {
        resolveText = resolve;
      });

      const stream = new ReadableStream({
        async start(controller) {
          try {
            for await (const chunk of result.textStream) {
              fullText += chunk;
              // Send content as Server-Sent Event
              controller.enqueue(
                encoder.encode(`event: message\ndata: ${JSON.stringify(chunk)}\n\n`),
              );
            }

            // Send completion event
            controller.enqueue(encoder.encode(`event: done\ndata: true\n\n`));
            resolveText(fullText);
          } catch (error) {
            console.error("SSE streaming error:", error);
            controller.enqueue(
              encoder.encode(
                `event: error\ndata: ${error instanceof Error ? error.message : "Streaming error"}\n\n`,
              ),
            );
            resolveText(fullText); // Resolve with partial text even on error
          } finally {
            controller.close();
          }
        },
      });

      return { stream, textPromise };
    } catch (error) {
      console.error("AI stream SSE error:", error);
      throw new AIError(
        error instanceof Error ? error.message : "Unknown AI SSE streaming error",
        error,
      );
    }
  }
}

// Custom error class for AI service errors
export class AIError extends Error {
  constructor(
    message: string,
    public cause?: unknown,
  ) {
    super(message);
    this.name = "AIError";
  }
}

// Singleton instance for convenience
export const aiService = new AIService();

