import { useEffect, useState } from "react";

interface TimeSeriesData {
  date: string;
  value: number;
}

interface ChartData {
  labels: string[];
  datasets: Array<{
    label: string;
    data: number[];
    borderColor: string;
    backgroundColor: string;
    tension: number;
  }>;
}

interface ChartOptions {
  responsive: boolean;
  maintainAspectRatio?: boolean;
  plugins: {
    legend: {
      display: boolean;
    };
    title: {
      display: boolean;
      text?: string;
    };
  };
  scales: {
    y: {
      beginAtZero: boolean;
      title?: {
        display: boolean;
        text?: string;
      };
    };
    x?: {
      display?: boolean;
      title?: {
        display: boolean;
        text?: string;
      };
    };
  };
}

interface ClientChartProps {
  title: string;
  data: TimeSeriesData[];
  color?: string;
  yAxisLabel?: string;
}

interface ChartData {
  labels: string[];
  datasets: Array<{
    label: string;
    data: number[];
    borderColor: string;
    backgroundColor: string;
    tension: number;
    fill: boolean;
  }>;
}

interface ChartOptions {
  responsive: boolean;
  maintainAspectRatio: boolean;
  plugins: {
    legend: { display: boolean };
    title: { display: boolean };
  };
  scales: {
    y: {
      beginAtZero: boolean;
      title: { display: boolean; text?: string };
    };
    x: {
      title: { display: boolean; text: string };
    };
  };
}

export function AnalyticsChart({
  title,
  data,
  color = "rgb(59, 130, 246)",
  yAxisLabel,
}: ClientChartProps) {
  const [Chart, setChart] = useState<React.ComponentType<{
    data: ChartData;
    options: ChartOptions;
  }> | null>(null);
  const [mounted, setMounted] = useState(false);

  useEffect(() => {
    setMounted(true);

    // Dynamic import only on client side
    Promise.all([import("chart.js"), import("react-chartjs-2")]).then(([ChartJS, { Line }]) => {
      ChartJS.Chart.register(
        ChartJS.CategoryScale,
        ChartJS.LinearScale,
        ChartJS.PointElement,
        ChartJS.LineElement,
        ChartJS.Title,
        ChartJS.Tooltip,
        ChartJS.Legend,
      );
      setChart(() => Line);
    });
  }, []);

  // Don't render anything during SSR
  if (!mounted) {
    return (
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
        <div className="h-64 flex items-center justify-center bg-gray-50 rounded">
          <p className="text-gray-500">Loading chart...</p>
        </div>
      </div>
    );
  }

  if (!Chart) {
    return (
      <div className="bg-white p-6 rounded-lg border">
        <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
        <div className="h-64 flex items-center justify-center bg-gray-50 rounded">
          <p className="text-gray-500">Loading chart...</p>
        </div>
      </div>
    );
  }

  const chartData = {
    labels: data.map((d) => d.date),
    datasets: [
      {
        label: title,
        data: data.map((d) => d.value),
        borderColor: color,
        backgroundColor: color.replace("rgb", "rgba").replace(")", ", 0.1)"),
        tension: 0.4,
        fill: true,
      },
    ],
  };

  const options = {
    responsive: true,
    maintainAspectRatio: false,
    plugins: {
      legend: {
        display: false,
      },
      title: {
        display: false,
      },
    },
    scales: {
      y: {
        beginAtZero: true,
        title: {
          display: !!yAxisLabel,
          text: yAxisLabel,
        },
      },
      x: {
        title: {
          display: true,
          text: "Date",
        },
      },
    },
  };

  return (
    <div className="bg-white p-6 rounded-lg border">
      <h3 className="text-lg font-semibold text-gray-900 mb-4">{title}</h3>
      <div className="h-64">
        <Chart data={chartData} options={options} />
      </div>
    </div>
  );
}

