// ABOUTME: Generic AI chat channel handler using createDataStreamResponse
// ABOUTME: Configurable for different AI chat types (planning, code review, general, etc.)

import type { ToolDefinition } from "~/lib/ai.server";
import { aiService } from "~/lib/ai.server";
import { aiConfig } from "~/lib/ai-config.server";
import { track } from "~/lib/analytics/track.server";
import { db } from "~/lib/db.server";
import { getAIChatConfig } from "../ai-chat-configs";
import type { ChannelParams, StreamSession } from "../types";
import { BaseChannelHandler } from "./base";

export class AIChatHandler extends BaseChannelHandler<"ai-chat"> {
  async validateAccess(userId: string, params: ChannelParams<"ai-chat">): Promise<boolean> {
    return await this.validateChatAccess(userId, params.chatId);
  }

  async subscribe(session: StreamSession, params: ChannelParams<"ai-chat">): Promise<void> {
    try {
      // Get configuration for this chat
      const config = getAIChatConfig(params.chatId);

      // Get chat and build conversation history
      const chat = await this.getChat(params.chatId);
      if (!chat) {
        this.sendToSession(session, "error", { message: "Chat not found" });
        return;
      }

      // Save user message if it doesn't already exist
      await this.saveUserMessage(params.chatId, params.message);

      // Track message sent
      await this.trackMessage(session.userId, params.chatId, params.message, chat);

      // Build conversation history
      const messages = chat.messages.map((msg: { role: string; content: string }) => ({
        role: msg.role as "user" | "assistant",
        content: msg.content,
      }));
      messages.push({ role: "user", content: params.message });

      // Send platform event: AI response starting
      this.sendToSession(session, "platform-event", {
        event: "ai_response_started",
        chatId: params.chatId,
        model: config.model || aiConfig.models.general,
        timestamp: new Date().toISOString(),
      });

      // Stream AI response using the AI service
      const { stream, textPromise } = await aiService.streamSSE({
        messages,
        systemPrompt: config.systemPrompt,
        model: config.model || aiConfig.models.general,
        temperature: config.temperature,
        maxTokens: config.maxTokens,
        tools: config.tools as Record<string, ToolDefinition> | undefined,
      });

      // Pipe AI stream to our session
      await this.pipeAIStreamToSession(session, stream);

      // Send platform event: AI response completed
      this.sendToSession(session, "platform-event", {
        event: "ai_response_completed",
        chatId: params.chatId,
        timestamp: new Date().toISOString(),
      });

      // Handle database operations when streaming completes
      textPromise
        .then(async (assistantMessage) => {
          if (assistantMessage) {
            const effectiveModel = config.model || aiConfig.models.general;
            await this.saveAssistantMessage(params.chatId, assistantMessage);
            await this.trackAIResponse(
              session.userId,
              params.chatId,
              assistantMessage,
              chat,
              effectiveModel,
            );
          }
        })
        .catch((error) => {
          this.logError("Error saving AI response:", error);
        });
    } catch (error) {
      this.logError("Failed to process AI chat request", error);
      this.sendToSession(session, "error", {
        message: error instanceof Error ? error.message : "Unknown error",
      });
    }
  }

  async unsubscribe(session: StreamSession): Promise<void> {
    this.log(`Unsubscribed AI chat session ${session.id}`);
  }

  private async getChat(chatId: string) {
    return await db.chat.findUnique({
      where: { id: chatId },
      include: {
        messages: {
          orderBy: { createdAt: "asc" },
        },
      },
    });
  }

  private async saveUserMessage(chatId: string, message: string) {
    const existingMessage = await db.message.findFirst({
      where: {
        chatId,
        role: "user",
        content: message,
      },
      orderBy: { createdAt: "desc" },
    });

    if (!existingMessage) {
      await db.message.create({
        data: {
          chatId,
          role: "user",
          content: message,
        },
      });
    }
  }

  private async trackMessage(
    userId: string,
    chatId: string,
    message: string,
    chat: { messages: Array<{ role: string; createdAt: Date | string }>; createdAt: Date | string },
  ) {
    const userMessages = chat.messages.filter((m) => m.role === "user");
    const assistantMessages = chat.messages.filter((m) => m.role === "assistant");
    const isFirstMessage = userMessages.length === 0;
    const sessionDuration = Date.now() - new Date(chat.createdAt).getTime();

    await track("message_sent", {
      chatId,
      userId,
      role: "user",
      messageLength: message.length,
      totalMessages: chat.messages.length + 1,
      userMessageCount: userMessages.length + 1,
      assistantMessageCount: assistantMessages.length,
      isFirstMessage,
      sessionDurationMinutes: Math.floor(sessionDuration / (1000 * 60)),
      conversationTurns: Math.min(userMessages.length, assistantMessages.length),
    });
  }

  private async pipeAIStreamToSession(session: StreamSession, aiStream: ReadableStream) {
    const reader = aiStream.getReader();
    const decoder = new TextDecoder();
    let sentDone = false;
    let buffer = "";

    try {
      while (true) {
        const { done, value } = await reader.read();
        if (done) break;

        // Buffer and parse SSE events robustly (delimited by blank line)
        buffer += decoder.decode(value, { stream: true });
        let sepIndex: number;
        while ((sepIndex = buffer.indexOf("\n\n")) !== -1) {
          const eventBlock = buffer.slice(0, sepIndex);
          buffer = buffer.slice(sepIndex + 2);

          let eventType = "message";
          const dataLines: string[] = [];
          for (const line of eventBlock.split("\n")) {
            if (line.startsWith("event:")) eventType = line.slice(6).trim();
            else if (line.startsWith("data:")) dataLines.push(line.slice(5).trimStart());
          }
          const data = dataLines.join("\n");

          if (eventType === "done") {
            this.sendToSession(session, "done", { complete: true });
            sentDone = true;
            continue;
          }
          if (eventType === "error") {
            this.sendToSession(session, "error", { message: "AI streaming error" });
            continue;
          }
          try {
            const parsed = JSON.parse(data);
            if (typeof parsed === "string") {
              this.sendToSession(session, "message", parsed);
            }
          } catch {
            // Ignore incomplete/invalid chunks
          }
        }
      }

      // Ensure done event is always sent
      if (!sentDone) {
        this.sendToSession(session, "done", { complete: true });
      }
    } catch (error) {
      this.logError("Error piping AI stream", error);
      this.sendToSession(session, "error", { message: "Streaming failed" });
    } finally {
      try {
        await reader.cancel();
      } catch {
        // Cancel might fail if stream already closed
      }
      reader.releaseLock();
    }
  }

  private async saveAssistantMessage(chatId: string, content: string) {
    await db.message.create({
      data: {
        chatId,
        role: "assistant",
        content,
      },
    });

    // Update chat timestamp
    await db.chat.update({
      where: { id: chatId },
      data: { updatedAt: new Date() },
    });

    // Update chat title if this is the first exchange
    const chat = await db.chat.findUnique({
      where: { id: chatId },
      include: { messages: true },
    });

    if (chat && chat.messages.length <= 2) {
      await db.chat.update({
        where: { id: chatId },
        data: {
          title:
            chat.messages[0]?.content.slice(0, 50) +
            (chat.messages[0]?.content.length > 50 ? "..." : ""),
        },
      });
    }
  }

  private async trackAIResponse(
    userId: string,
    chatId: string,
    content: string,
    chat: { messages: Array<{ role: string }> },
    model: string,
  ) {
    const responseWords = content.split(/\s+/).length;
    const hasQuestion = content.includes("?");
    const hasMultipleChoice =
      content.includes("A)") || content.includes("1.") || content.includes("Option");
    const hasStructuredOutput =
      content.includes("##") || content.includes("**") || content.includes("-");

    await track("ai_response_generated", {
      chatId,
      userId,
      responseLength: content.length,
      responseWords,
      conversationTurn: Math.floor(chat.messages.length / 2) + 1,
      isFirstExchange: chat.messages.length === 0,
      hasQuestion,
      hasMultipleChoice,
      hasStructuredOutput,
      model,
    });
  }
}

