// ABOUTME: Generic streaming platform endpoint with channel routing
// ABOUTME: Routes streaming requests to appropriate channel handlers

import type { Route } from "./+types/api.stream";

export async function loader({ request }: Route.LoaderArgs) {
  // Handle GET requests to streaming endpoint
  const url = new URL(request.url);
  const channel = url.searchParams.get("channel");
  const sessionId = url.searchParams.get("sessionId");

  if (!channel) {
    return new Response("Missing channel parameter", { status: 400 });
  }

  // Import server-only modules dynamically to avoid client bundling
  const { requireUser } = await import("~/lib/session.server");
  const { streamingService } = await import("~/lib/streaming/service.server");

  try {
    const sessionUser = await requireUser(request);

    // If sessionId provided, return session status (simplified for now)
    if (sessionId) {
      return Response.json({ status: "unknown", sessionId });
    }

    // Otherwise return available channels or status
    return Response.json({
      status: "ready",
      availableChannels: ["ai-chat"],
      userId: sessionUser.id,
    });
  } catch (error) {
    console.error("Stream loader error:", error);
    return new Response("Unauthorized", { status: 401 });
  }
}

export async function action({ request }: Route.ActionArgs) {
  // Import server-only modules dynamically to avoid client bundling
  const { requireUser } = await import("~/lib/session.server");
  const { streamingService } = await import("~/lib/streaming/service.server");
  const { findOrCreateUser } = await import("~/lib/user.server");
  try {
    const sessionUser = await requireUser(request);
    const { channel, params } = await request.json();

    if (!channel || !params) {
      return new Response("Missing channel or params", { status: 400 });
    }

    // Ensure user exists in database
    const dbUser = await findOrCreateUser(sessionUser);

    // Create streaming response
    const stream = new ReadableStream({
      async start(controller) {
        let session: Awaited<ReturnType<typeof streamingService.createSession>> | undefined;

        try {
          // Create session with the streaming service
          session = await streamingService.createSession(dbUser.id, channel, params, controller);

          console.log(`Started streaming session: ${session.id} for channel: ${channel}`);
        } catch (error) {
          console.error("Failed to create streaming session:", error);

          const encoder = new TextEncoder();
          controller.enqueue(
            encoder.encode(
              `event: error\ndata: ${JSON.stringify({
                message: error instanceof Error ? error.message : "Failed to start stream",
              })}\n\n`,
            ),
          );
          controller.close();
        }

        // Handle cleanup on client disconnect
        request.signal.addEventListener("abort", async () => {
          if (session) {
            console.log(`Client disconnected, cleaning up session: ${session.id}`);
            await streamingService.destroySession(session.id);
          }
        });
      },
    });

    return new Response(stream, {
      headers: {
        "Content-Type": "text/event-stream",
        "Cache-Control": "no-cache",
        Connection: "keep-alive",
        "Access-Control-Allow-Origin": "*",
        "Access-Control-Allow-Headers": "Content-Type, Authorization",
      },
    });
  } catch (error) {
    console.error("Stream endpoint error:", error);
    return new Response(error instanceof Error ? error.message : "Unknown error", { status: 500 });
  }
}

